'use strict';

var Benchmark = require('benchmark');

var suite = new Benchmark.Suite();

var testData = require('./test.json');

var stringifyPackages = {
  // 'JSON.stringify': JSON.stringify,
  'fast-json-stable-stringify': require('../index'),
  'json-stable-stringify': true,
  'fast-stable-stringify': true,
  'faster-stable-stringify': true
};

var _loop = function _loop(name) {
  var func = stringifyPackages[name];
  if (func === true) func = require(name);
  suite.add(name, function () {
    func(testData);
  });
};

for (var name in stringifyPackages) {
  _loop(name);
}

suite.on('cycle', function (event) {
  return console.log(String(event.target));
}).on('complete', function () {
  console.log('The fastest is ' + this.filter('fastest').map('name'));
}).run({
  async: true
});