'use strict';

var each = require('../helpers/each');
/*!
 * ignore
 */


module.exports = function (schema) {
  var unshift = true;
  schema.s.hooks.pre('remove', false, function (next) {
    if (this.ownerDocument) {
      next();
      return;
    }

    var _this = this;

    var subdocs = this.$getAllSubdocs();
    each(subdocs, function (subdoc, cb) {
      subdoc.$__remove(cb);
    }, function (error) {
      if (error) {
        return _this.$__schema.s.hooks.execPost('remove:error', _this, [_this], {
          error: error
        }, function (error) {
          next(error);
        });
      }

      next();
    });
  }, null, unshift);
};